        let term2 = app!("foo", [42, true]);
        let mut env = Env::default();

        let mut ground_truth = HashMap::new();
        ground_truth.insert("X".into(), 42.into());
        ground_truth.insert("Y".into(), true.into());
        term1.unify_in_place_helper(&term2, &mut env).unwrap();
